import{_ as o,c as i,o as l,ah as t}from"./chunks/framework.CvbyeFFO.js";const h=JSON.parse('{"title":"OpenAI 系列","description":"","frontmatter":{"title":"OpenAI 系列","date":"2025-12-06T11:52:04.000Z","create":"2025-12-06T11:52:04.000Z","categories":["openai"],"tags":["gpt5","gpt5-codex","gpt-oss","o1","o1mini","o3","o3-mini","GPT4","ChatGPT","InstructGPT","CodeX"]},"headers":[],"relativePath":"posts/llm/industry/mainllm/07-openai-series.md","filePath":"posts/llm/industry/mainllm/07-openai-series.md","lastUpdated":null}'),a={name:"posts/llm/industry/mainllm/07-openai-series.md"};function c(s,e,r,d,n,u){return l(),i("div",null,e[0]||(e[0]=[t('<h2 id="_2509-gpt5-codex" tabindex="-1">(2509 )GPT5-Codex <a class="header-anchor" href="#_2509-gpt5-codex" aria-label="Permalink to &quot;(2509 )GPT5-Codex&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li><a href="https://openai.com/zh-Hans-CN/index/introducing-upgrades-to-codex/" target="_blank" rel="noreferrer">Codex 全面升级</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">GPT5-Codex 概览</div><p><strong>核心技术</strong></p><ul><li>Agentic Coding，关键技术： <ul><li><code>真实软件工程</code> <code>RL学习</code>，</li><li><code>沙盒执行</code>，</li><li>可控工具使用等。</li></ul></li></ul><p><strong>关键结果</strong></p><ul><li>外部评论：效果比GPT5好</li></ul></div><h2 id="_2508-gpt5" tabindex="-1">(2508) GPT5 <a class="header-anchor" href="#_2508-gpt5" aria-label="Permalink to &quot;(2508) GPT5&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li>(2508) <a href="https://openai.com/zh-Hans-CN/index/gpt-5-system-card/" target="_blank" rel="noreferrer">GPT-5 系统卡</a></li><li>(2508) <a href="https://openai.com/zh-Hans-CN/index/introducing-gpt-5-for-developers/" target="_blank" rel="noreferrer">隆重推出面向开发人员的 GPT-5</a></li><li>(2508) <a href="https://openai.com/zh-Hans-CN/index/introducing-gpt-5/" target="_blank" rel="noreferrer">隆重推出 GPT-5</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">GPT5 概览</div><p><strong>GPT5</strong></p><ul><li>最强大的代码模型。</li></ul></div><h2 id="_2508-gpt-oss" tabindex="-1">(2508) GPT-OSS <a class="header-anchor" href="#_2508-gpt-oss" aria-label="Permalink to &quot;(2508) GPT-OSS&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li><a href="https://openai.com/zh-Hans-CN/index/introducing-gpt-oss/" target="_blank" rel="noreferrer">隆重推出 gpt-oss</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">GPT-OSS 概览</div><p><strong>核心</strong></p><ul><li>MoE，OpenAI 开源。</li><li>gpt-oss-120b, gpt-oss-20b</li></ul></div><h2 id="_2409-o系列" tabindex="-1">(2409) O系列 <a class="header-anchor" href="#_2409-o系列" aria-label="Permalink to &quot;(2409) O系列&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li>(2409) <a href="https://openai.com/zh-Hans-CN/index/introducing-openai-o1-preview/" target="_blank" rel="noreferrer">隆重推出 OpenAI o1-preview</a></li><li>(2504) <a href="https://openai.com/zh-Hans-CN/index/introducing-o3-and-o4-mini/" target="_blank" rel="noreferrer">隆重推出 OpenAI o3 和 o4-mini</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">O系列概览</div><p><strong>核心思想</strong></p><ul><li>以<code>推理思维链</code>、<code>内部深思</code>为核心。旨在解决复杂问题，比如编程等。</li></ul><p><strong>o1, o1-mini</strong></p><ul><li>首次引入 Step-by-step 内部推理，思考后再做回答。</li><li>o1-mini在软件任务上效果好。</li></ul><p><strong>o3, o3-mini</strong></p><ul><li><code>扩展上下文长度</code>、<code>优化了repo-level</code>的代码编辑能力等。</li><li>SWE-Bench超越GPT4。</li></ul></div><h2 id="_2309-gpt4" tabindex="-1">(2309) GPT4 <a class="header-anchor" href="#_2309-gpt4" aria-label="Permalink to &quot;(2309) GPT4&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li>(2309)<a href="https://openai.com/index/gpt-4v-system-card/" target="_blank" rel="noreferrer">GPT-4V(ision) system card</a></li><li>(2405) <a href="https://openai.com/zh-Hans-CN/index/hello-gpt-4o/" target="_blank" rel="noreferrer">你好 GPT-4o</a></li><li>(2407) <a href="https://openai.com/zh-Hans-CN/index/gpt-4o-mini-advancing-cost-efficient-intelligence/" target="_blank" rel="noreferrer">GPT-4o mini：推动高性价比智能的发展</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">GPT4 概览</div><p><strong>关键结果</strong></p><ul><li>相比GPT3，具有更强推理和代码能力。</li><li>GPT4-Turbo：提高生产环境效率。</li><li>GPT-4o：多模态，集成文本、<code>图像</code>和音频， 保留强大代码能力。</li><li>GPT-4o-mini：强调成本效率。</li><li>GPT-4.1：<code>扩展长上下文</code>和代码编辑能力，可在Repo-Level做编辑。</li></ul></div><h2 id="_2211-chatgpt-gpt3-5" tabindex="-1">(2211) ChatGPT/GPT3.5 <a class="header-anchor" href="#_2211-chatgpt-gpt3-5" aria-label="Permalink to &quot;(2211) ChatGPT/GPT3.5&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li>(2211) <a href="https://openai.com/zh-Hans-CN/index/chatgpt/" target="_blank" rel="noreferrer">隆重推出 ChatGPT</a></li><li>(2303) <a href="https://openai.com/index/chatgpt-plugins/##code-interpreter" target="_blank" rel="noreferrer">ChatGPT plugins</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">ChatGPT 概览</div><p><strong>核心技术</strong></p><ul><li>基于InstructGPT开发，GPT3拓展版本，增加以下能力 <ul><li>额外的<code>指令微调</code>和<code>RLHF</code></li><li><code>稳定的多轮对话能力</code>，最核心。</li><li>增加<code>安全拒识行为</code>等内容。</li></ul></li></ul><p><strong>关键结果</strong></p><ul><li>大火。</li><li>第一个被广泛应用的对话式LLM。</li><li>具有较好编程能力，在各IDE工作流中使用。</li></ul></div><h2 id="_2203-instructgpt" tabindex="-1">(2203) InstructGPT <a class="header-anchor" href="#_2203-instructgpt" aria-label="Permalink to &quot;(2203) InstructGPT&quot;">​</a></h2><div class="custom-block caution"><div class="custom-block-title">参考文章</div><ul><li><a href="https://arxiv.org/abs/2203.02155" target="_blank" rel="noreferrer">Training language models to follow instructions with human feedback</a></li></ul></div><div class="custom-block tip"><div class="custom-block-title">Instruct GPT 概览</div><p><strong>问题背景</strong></p><ul><li>GPT3虽然懂得多，但<code>经常答非所问</code>、<code>不安全</code>、和人类需求不对齐。</li></ul><p><strong>核心技术</strong></p><ul><li>通过<code>人类反馈RLHF</code>，和<code>人类偏好进行对齐</code>。OpenAI 护城河。</li><li>人类偏好 <code>Reward Mode</code>l (偏好打分)、<code>PPO</code>训练等。</li></ul><p><strong>关键结果</strong></p><ul><li>模型更受人类青睐：<code>幻觉更少</code>、<code>行为更安全</code>。</li><li><code>小参数对齐模型</code> <code>超越</code> <code>大参数GPT3</code>模型</li></ul></div><h2 id="code-x" tabindex="-1">Code X <a class="header-anchor" href="#code-x" aria-label="Permalink to &quot;Code X&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">Code X 概览</div><p><strong>核心技术</strong></p><ul><li>GPT3编程特别版，<code>GPT3</code>+<code>GitHub代码预训练</code>。</li></ul><p><strong>关键结果</strong></p><ul><li>在<code>HumanEval代码生成</code>和补全Benchmark上表现不错，<code>早期的代码对齐LLM</code>。</li><li>催生了Github Copilot这种商业产品，解决了<code>自然语言</code> -&gt; <code>可执行代码</code>的翻译问题。</li></ul></div><h2 id="gpt-3" tabindex="-1">GPT 3 <a class="header-anchor" href="#gpt-3" aria-label="Permalink to &quot;GPT 3&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">GPT3 概览</div><p><strong>核心技术</strong></p><ul><li>在<code>大规模web</code>和<code>文本</code>数据上做<code>ScaleUp</code>。</li></ul><p><strong>关键结果</strong></p><ul><li>强大的<code>上下文学习能力</code>(zero-shot/few-shot)，<code>无需梯度更新</code>也能适应理解、代码、推理等任务。</li></ul></div><h2 id="gpt-1-2" tabindex="-1">GPT 1-2 <a class="header-anchor" href="#gpt-1-2" aria-label="Permalink to &quot;GPT 1-2&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">GPT1-2 系列概览</div><p><strong>核心</strong></p><ul><li>验证了<code>生成式预训练</code>可行，开源了权重。</li></ul></div>',27)]))}const g=o(a,[["render",c]]);export{h as __pageData,g as default};
