import{_ as l,c as o,o as a,ah as i}from"./chunks/framework.CvbyeFFO.js";const g=JSON.parse('{"title":"Claude 系列","description":"","frontmatter":{"title":"Claude 系列","date":"2025-12-06T11:55:44.000Z","create":"2025-12-06T11:55:44.000Z","categories":["claude"],"tags":["claude3","claude4","claude 4.5"]},"headers":[],"relativePath":"posts/llm/industry/mainllm/09-claude-series.md","filePath":"posts/llm/industry/mainllm/09-claude-series.md","lastUpdated":null}'),t={name:"posts/llm/industry/mainllm/09-claude-series.md"};function d(c,e,r,s,u,n){return a(),o("div",null,e[0]||(e[0]=[i('<h2 id="概览" tabindex="-1">概览 <a class="header-anchor" href="#概览" aria-label="Permalink to &quot;概览&quot;">​</a></h2><div class="custom-block note"><div class="custom-block-title">笔记</div><p><strong>核心转变</strong></p><ul><li>RLHF+宪法AI对齐的大模型，编程 具备上下文、工具增强、agentic的编程模型。</li></ul><p><strong>整体脉络</strong></p><ul><li><strong>Claude 1→2：</strong><ul><li>增加<code>长上下文窗口</code>和<code>更安全的指令遵循能力</code>，提升了标准化代码合成与编辑的表现。</li></ul></li><li><strong>Claude 3/3.5：</strong><ul><li>引入<code>原生多模态</code>和<code>函数调用功能</code></li><li>在<code>沙箱评估环境</code>下，其在 <code>HumanEval </code>测试和多文件仓库编辑方面的<code>性能有显著提升</code> 。</li></ul></li><li><strong>Claude 4/4.5：</strong><ul><li>整合了<code>深思熟虑的推理能力</code>(deliberative reasoning)和<code>Computer-Use能力</code>(Terminal、IDE、包管理器、browse）</li><li>并结合了策略控制的工具使用及并行的测试时计算，在repo-level和终端编程套件上展现了强大的结果。</li></ul></li></ul></div><h2 id="数据集" tabindex="-1">数据集 <a class="header-anchor" href="#数据集" aria-label="Permalink to &quot;数据集&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">提示</div><ul><li>(2512) Anthropic Interviewer：<a href="https://www.anthropic.com/research/anthropic-interviewer" target="_blank" rel="noreferrer">blog</a>, <a href="https://huggingface.co/datasets/Anthropic/AnthropicInterviewer" target="_blank" rel="noreferrer">data</a></li></ul></div><h2 id="claude-4-系列" tabindex="-1">Claude 4 系列 <a class="header-anchor" href="#claude-4-系列" aria-label="Permalink to &quot;Claude 4 系列&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">Claude4 系列</div><p><strong>核心技术</strong></p><ul><li>结合<code>深度思考</code>和<code>Computer-Use技术</code>（包括<code>沙箱</code>、<code>编辑器</code>、<code>包管理器</code>、<code>浏览器</code>）</li><li>并通过<code>RLHF</code>和<code>宪法AI</code>进行训练和对齐。</li></ul><p><strong>关键结果</strong></p><ul><li><code>长跨度</code>、<code>工具增强</code>的code agent。</li><li>Claude4在SWE-Bench效果好，Claude4.5提升仓库级修复能力，在terminal和工具使用显示出效果。</li></ul><p><strong>定位</strong></p><ul><li>自主代理（Agent）。</li><li>它不再只是生成代码文本，而是像人一样操作终端（Terminal）、使用编辑器、运行浏览器查资料、安装依赖包。</li></ul></div><h2 id="claude-3-系列" tabindex="-1">Claude 3 系列 <a class="header-anchor" href="#claude-3-系列" aria-label="Permalink to &quot;Claude 3 系列&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">Claude3</div><p><strong>核心技术</strong></p><ul><li><code>原生多模态</code>(看懂UI或架构图)、<code>函数调用</code>、<code>仓库级编辑</code></li><li>Claude3(Opus/Sonnet/Haiku)：支持原生工具调用和视觉输入的多模态模型。</li><li>Claude3.5 Sonnet 进一步提升代码性能，在仓库级多代码编辑，展现出优势。</li></ul><p><strong>关键结果</strong></p><ul><li>多模态、长上下文、Repo-Level，支持<code>大规模代码库的理解</code>。</li></ul><p><strong>定位</strong></p><ul><li>高级助手。不仅能写代码，还能理解整个项目结构，并且“看”得懂需求。</li></ul></div><h2 id="claude-1和2系列" tabindex="-1">Claude 1和2系列 <a class="header-anchor" href="#claude-1和2系列" aria-label="Permalink to &quot;Claude 1和2系列&quot;">​</a></h2><div class="custom-block tip"><div class="custom-block-title">Claude 1&amp;2</div><p><strong>Claude1</strong></p><ul><li>基础： RLHF（让模型听话）、Constitutional AI（让模型安全）</li><li>扩展： <ul><li>长上下文（能读长代码）</li><li>更安全的指令跟随</li><li>结构化输出</li></ul></li></ul><p><strong>Claude2</strong></p><ul><li>扩展上下文</li><li>针对多步推理和工具友好型格式，在训练和服务期间做了改进。辅助了对代码的理解。</li></ul><p><strong>定位</strong></p><ul><li>辅助工具。能<code>写代码片段</code>、<code>解释代码</code>，但主要还是处理文本和简单的逻辑。</li></ul></div>',10)]))}const m=l(t,[["render",d]]);export{g as __pageData,m as default};
